mapper.select:
SELECT id, sec_len, up_min, down_min, cat_nam, mntn_nm
FROM RouteProperties
WHERE id = #{id}

mapper.update:
UPDATE RouteProperties
SET sec_len = #{secLen}, up_min = #{upMin}, down_min = #{downMin}, cat_nam = #{catNam}, mntn_nm = #{mntnNm}
WHERE id = #{id}

mapper.select:
SELECT id, geometry
FROM RouteGeometry
WHERE id = #{id}

mapper.update:
UPDATE RouteGeometry
SET geometry = ST_GeomFromText(#{multiLineString})
WHERE id = #{id}
