mapper.select:
SELECT CASE WHEN COUNT(*) = 0 THEN 'N' ELSE 'Y' END AS EXISTS_YN
FROM USER_INFO
WHERE USER_ID = #{userId}

SELECT CASE WHEN COUNT(*) = 0 THEN 'N' ELSE 'Y' END AS EXISTS_YN
FROM USER_INFO
WHERE EMAIL = #{email}

SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE USER_ID = #{userId}
  AND PASSWORD = #{password}

SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE 1=1

    AND USER_ID = #{userId}

AND USER_NAME = #{userName}
AND EMAIL = #{email}

mapper.update:
UPDATE USER_INFO
SET PASSWORD = #{password}
WHERE USER_ID = #{userId}
